function App() {
  const cartShow = useSelector(state => state.ui.cartShow);
  const cart = useSelector(state => state.cart);

  useEffect(() => {
    fetch('https://react-http-c3510-default-rtdb.firebaseio.com/cart.json', {
      method: 'PUT',
      body: JSON.stringify(cart)
    })
  }, [cart]);

  return (
    <Fragment>
    <Layout>
     {cartShow && <Cart /> }
      <Products />
    </Layout>
    </Fragment>
  );
}

export default App;
